(function (jq) {

    var self = this;

    var template = '<div data-notify="container" class="col-xs-11 col-sm-3 alert alert-{0}" role="alert">' +
        '<span data-notify="title">{1}  </span>' +
        '<span data-notify="message">{2}</span>' +
        '</div>';

    function _notifyAdd(message) {
        $('#priceAlertsButton').show();
        $('#hideNotificationsCounter').show();
        var cantidad = eval($('#notificationCount').html()) + 1;
        if (cantidad > 8) {
            cantidad = 1;
            $('#NotificationBox').html('');
        }
        $('#notificationCount').html(cantidad);
        var html = $('#NotificationBox').html() + "<div class='notifibordeizq'>" + message + "</div>";
        $('#NotificationBox').html(html);
        beep();
    }

    
    function _notifyremove() {
        //$('#priceAlertsButton').hide();
        $('#hideNotificationsCounter').hide();
        $('#notificationCount').html(0);
        $('#NotificationBox').html("").hide();
    }

    function _notifyAddTrade(item) {
        if (item.CommandName === "Trade") {
            var message = "<br>Se creo una nueva Orden nª: <color style='color: lightskyblue;'>" + item.orderId2 + "</color>";
            _notifyAdd(message);
        }
    }

    function _notifyAddRejectMarket(item) {
        var message = "<br>Se rechazo una oferta. Motivo rechazo: <color style='color: coral;'>" + item.comment + "</color>";
        _notifyAdd(message);
        _notifyError("<color style='font-size: 12px; font-family: Roboto,sans-serif!important'>" + item.comment + "<color>", "<color style='font-size: 12px; font-family: Roboto,sans-serif!important'>  Se rechazo una oferta: </color >");
    }

    function _notifyAddCancelMarket(item) {
        var message = "<br>Se cancelo en mercado la Orden nº: <color style='color: coral;'>" + item.orderId2 + "</color>";
        _notifyAdd(message);
    }

    function _notify(item) {
        var tipo = item.side === 1 ? '<font color="#e53935">Bid</font>' : '<font color="#00b276">Ask</font>';
        var color = item.side === 1 ? 'alert-pastel-rojo' : 'alert-pastel-verde';
        var mensaje = '<br> Send new ' + tipo + ' Order of ' + item.symbo_name + ' <br> Price: ' + item.price + ' <br> Volume: ' + item.volume;
    
        $.notify({
            title: 'Notification',
            message: mensaje
        }, {
                type: color,
                delay: 2000,
                template: template

            });

        _notifyAdd(mensaje);
    }

    function _notifyError(item, titulo) {
        var mensaje = item;
        var color = 'error-pastel-rojo-error';

        $.notify({
            title: '<i class="fas fa-exclamation-triangle" style="color:red">'+titulo+'</i>',
            message: mensaje
        },
            {
                type: color,
                delay: 5000,
                placement: {
                    from: "bottom",
                    align: "right"
                }
            });
    }

    function  _notifyMarket(isclose)
    {
     

        var color = isclose ? 'alert-pastel-rojo' : 'alert-pastel-verde';

        var mensaje = isclose?MAE.I18nService.getString("MARKET.CLOSE") : MAE.I18nService.getString("MARKET.OPEN");

        $.notify({
            title: '<i class="fas fa-bell"></i>',
            message: mensaje
        }, {
                type: color,
                delay: 1000,
                template: template
            });   
         
    }

    function beep() {
        var snd = new Audio("data:audio/wav;base64,");
        snd.play();
    }

    jq.extend(true, window, {
        MAE: {
            NotifyService:
            {
                notify : _notify,     
                notifyMarket  : _notifyMarket,
                notifyError : _notifyError,
                notifyremove: _notifyremove,
                notifyAdd: _notifyAdd,
                notifyAddTrade: _notifyAddTrade,
                notifyAddRejectMarket: _notifyAddRejectMarket,
                notifyAddCancelMarket: _notifyAddCancelMarket
            }
        }
    });

})(jQuery);